mapper.select:

select *
	from (
		select (row_number() over()) as numbering, t.*
			from (
							select *
							from board
							where delyn = false and

							subject ilike '%' || #{search} || '%'
							content ilike '%' || #{search} || '%'
							usrname ilike '%' || #{search} || '%'
							subject ilike '%' || #{search} || '%' or content ilike '%' || #{search} || '%'
							1 = 1

							order by refgroup desc, reforder
							) t
			) s
where numbering > (#{nowPage} - 1) * #{cntPerPage} and numbering <= #{nowPage} * #{cntPerPage}

mapper.delete:
update board 
set delyn = 'true', deldate = now() 
where num = #{num}

mapper.select:
select *
	from (
		select (row_number() over()) as numbering, t.*
			from (
							select *
							from board
							where delyn = false and
							subject ilike '%' || #{search} || '%'
							content ilike '%' || #{search} || '%'
							usrname ilike '%' || #{search} || '%'
							subject ilike '%' || #{search} || '%' or content ilike '%' || #{search} || '%'
							1 = 1
							order by num desc
							) t
			) s
where num = #{num}

mapper.update:
update board 
set cnt = cnt + 1 
where num = #{num}

update board 
set subject = #{subject}, content = #{content} 
where num = #{num}

mapper.select:
SELECT CASE WHEN EXISTS (
    SELECT *
    FROM board
    WHERE pwd = #{confirmPwd}
    and num = #{num}
)
THEN CAST(1 AS BIT)
ELSE CAST(0 AS BIT) end;

mapper.update:
update board set reforder = reforder + 1
where refgroup = #{refgroup}

and reforder > #{reforder}

mapper.select:
select count(*) 
from users 
where id = #{id};

select count(*) 
from users 
where id = #{id}
and password = #{pwd};
